import * as THREE from "three";
import FOG from "vanta/src/vanta.fog"; // 引入FOG效果

export default {
  name: 'UserLogin',
  data() {
    return {};
  },
  methods: {},
  mounted() {
    // 在mounted中初始化VANTA效果
    this.vantaEffect = FOG({
      el: this.$refs.vantaRef,
      // 绑定到ref
      THREE: THREE,
      mouseControls: true,
      // 鼠标控制
      touchControls: true,
      // 触摸控制
      gyroControls: false,
      // 陀螺仪控制
      minHeight: 200.0,
      // 最小高度
      minWidth: 200.0,
      // 最小宽度
      highlightColor: 0x008cba,
      // 突出色调
      midtoneColor: 0x0094d9,
      // 中间色调
      lowlightColor: 0x00b4f4,
      // 暗色调
      baseColor: 0xffffff,
      // 低色调
      blurFactor: 0.52,
      // 模糊因素
      speed: 1.2,
      // 速度
      zoom: 0.3 // 变焦
    });
  },
  beforeDestroy() {
    // 销毁VANTA效果
    if (this.vantaEffect) {
      this.vantaEffect.destroy();
    }
  }
};
